	struct Day1Result {
		// The floor Santa will end up being on.
		int endingFloor{ 0 };

		// The position of the first ')' character that will make Santa be on a negative floor(<0).
		int firstTimeUndergroundPosition{ 0 };
	};

	void solve()
	{
		std::cout << "Starting to solve Day1\n";
		int currentFloor{ 0 };
		Day1Result result;

		bool wentNegativeYet{ false };
		for (std::string::size_type i = 0; i < input.size(); ++i) {
			char c{ input[i] };

			if (c == '(') {
				++currentFloor;
			}
			else if (c == ')') {
				--currentFloor;
			}
			else {
				std::cout << "Invalid character: " << c << "\n";
			}

			if (!wentNegativeYet && currentFloor < 0) {
				result.firstTimeUndergroundPosition = i + 1;
				wentNegativeYet = true;
			}
		}
		result.endingFloor = currentFloor;

		std::cout << "Part1: " << result.endingFloor << "\n";
		std::cout << "Part2: " << result.firstTimeUndergroundPosition << "\n";
		std::cout << "Day1 solved.\n";
	}
