	void solve()
	{
		std::cout << "Starting to solve Day1\n";
		int floor{ 0 };

		// will contain the position(index + 1) of the character that made santa go to a negative floor for the first time.
		int part2Result{};
		bool part2Solved{ false };
		for (std::string::size_type i = 0; i < input.size(); ++i) {
			char c{ input[i] };

			if (c == '(') {
				++floor;
			}
			else if (c == ')') {
				--floor;
			}
			else {
				std::cout << "Invalid character: " << c << "\n";
			}

			if (!part2Solved && floor < 0) {
				part2Result = i + 1;
				part2Solved = true;
			}
		}

		std::cout << "Part1: " << floor << "\n";
		std::cout << "Part2: " << part2Result << "\n";
		std::cout << "Day1 solved.\n";
	}
